namespace HocusFocus.UI.MVVM.ViewModels;public class ProcessViewModel(string name, bool isChecked) : Bindable{    public string Name { get; set; } = name;    public bool IsChecked    {        get => isChecked;        set        {            if (isChecked != value)            {                isChecked = value;                OnPropertyChanged(nameof(IsChecked));            }        }    }}