using System.ComponentModel;namespace HocusFocus.UI.MVVM.Models;public class ProcessItem : INotifyPropertyChanged{    private bool _isChecked;    public string Name { get; set; }    public bool IsChecked    {        get => _isChecked;        set        {            if (_isChecked != value)            {                _isChecked = value;                OnPropertyChanged(nameof(IsChecked));            }        }    }    public event PropertyChangedEventHandler PropertyChanged;    protected void OnPropertyChanged(string propertyName)    {        PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propertyName));    }}