using System.Windows;using System.Windows.Controls;namespace HocusFocus.UI.Resources.Controls{    public partial class ProcessListItem : UserControl    {        public static readonly DependencyProperty ProcessNameProperty =            DependencyProperty.Register(nameof(ProcessName), typeof(string), typeof(ProcessListItem), new PropertyMetadata(default(string)));        public static readonly DependencyProperty IsCheckedProperty =            DependencyProperty.Register(nameof(IsChecked), typeof(bool), typeof(ProcessListItem), new PropertyMetadata(default(bool)));        public string ProcessName        {            get => (string)GetValue(ProcessNameProperty);            set => SetValue(ProcessNameProperty, value);        }        public bool IsChecked        {            get => (bool)GetValue(IsCheckedProperty);            set => SetValue(IsCheckedProperty, value);        }        public ProcessListItem()        {            InitializeComponent();        }    }}